CREATE TABLE "S_GUIA"."SISTEMA_PARAMETRO" 
   (	"VCODPARAMETRO" VARCHAR2(20) NOT NULL, 
	"NCODIGO" NUMBER(5,0) NOT NULL, 
	"VDESCRIPCION" VARCHAR2(40), 
	"NESTADO" NUMBER(4,0), 
	"NID" NUMBER(4,0), 
	"VVALOR" VARCHAR2(1000), 
	"VRESCRE" VARCHAR2(15), 
	"DFECCRE" DATE, 
	"VRESACT" VARCHAR2(15), 
	"DFECACT" DATE, 
CONSTRAINT "PARAMETRO_PK" PRIMARY KEY ("VCODPARAMETRO", "NCODIGO"))

COMMENT ON COLUMN "S_GUIA"."SISTEMA_PARAMETRO"."VCODPARAMETRO" IS 'CODIGO DEL  PARAMETRO';
COMMENT ON COLUMN "S_GUIA"."SISTEMA_PARAMETRO"."NCODIGO" IS 'ID DE LA TABLA PARAMETRO';
