COMMENT ON COLUMN "S_GUIA"."SISTEMA_PARAMETRO"."NESTADO" IS 'ESTADO DEL PARAMETRO, ACTIVO INACTIVO';
COMMENT ON COLUMN "S_GUIA"."SISTEMA_PARAMETRO"."NID" IS 'ID PARA LAS LISTAS';
COMMENT ON COLUMN "S_GUIA"."SISTEMA_PARAMETRO"."VVALOR" IS 'VALOR PARA PARAMETROS FIJOS';
COMMENT ON COLUMN "S_GUIA"."SISTEMA_PARAMETRO"."VRESCRE" IS 'USUARIO CREACION';
COMMENT ON COLUMN "S_GUIA"."SISTEMA_PARAMETRO"."DFECCRE" IS 'FECHA CREACION';
COMMENT ON COLUMN "S_GUIA"."SISTEMA_PARAMETRO"."VRESACT" IS 'USUARIO MODIFICACION';
COMMENT ON COLUMN "S_GUIA"."SISTEMA_PARAMETRO"."DFECACT" IS 'USUARIO MODIFICACION';
   
   
